mapper.select:
SELECT *
FROM user_sale_image usi
WHERE usi.pending_sale_id = #{pendingSaleId}

SELECT *
FROM verified_sale_image vsi
WHERE vsi.verified_sale_id = #{verifiedSaleId}

select p.main_image
from product p
where p.product_id = #{productId}

select m.nick_name
from member m
where m.member_id = #{memberId}
